sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//Get a GlideRecord object for the event record
	var event = current.event_instance.getRefRecord();

	//Check if the event is full
	//If the event is full set registration_status to wait list
	if (event.getDisplayValue('full') == true) {
		current.setValue('registration_status', 'Wait List');
	}
	else {
		//If the event isn't full check if Auto-approve Registration is present in the event options list
		if (event.getValue('options').split(',').indexOf('Auto-approve Registration') > -1) {

			//Add the user to the registered_users list on the event
			var registered_users = event.getValue('registered_users').split(',');
			registered_users.push(current.getValue('user'));
			event.setValue('registered_users', registered_users);

			//Ensure the sys_update_on and sys_updated_by timestamps aren't updated by this update
			event.autoSysFields(false);
			event.update();

			//Set the current registration_status to registered
			current.setValue('registration_status', 'Registered');
		}
	}


})(current, previous);
