sys_script_fix.script:
(function () {
	var gr = new GlideRecord('x_wegu_te_locations');
	gr.query();

	while (gr.next()) {
		var data = {};
		data.name = gr.getValue('name');
		data.capacity = gr.getValue('capacity');
		data.active = gr.getDisplayValue('active');

		createLocation(data);
	}

	function createLocation (data) {
		var gr = new GlideRecord('x_wegu_er_location');
		gr.newRecord();
		gr.setValue('name', data.name);
		gr.setValue('capacity', data.capacity);
		gr.setValue('active', data.active);
		gr.insert();
	} 

})();
